document.addEventListener("DOMContentLoaded",()=>{const e=Array.prototype.slice.call(document.querySelectorAll(".navbar-burger"),0),t=document.querySelector("nav");if(e.length<1)return;e.forEach(e=>{e.addEventListener("click",()=>{e.classList.toggle("nav--active"),t.classList.toggle("nav--active")})})});var elements=document.querySelectorAll("p");Array.prototype.forEach.call(elements,function(e){if(e.innerHTML=="[expand]"){var n=e.parentNode.innerHTML.replace("<p>[expand]</p>",'<div class="expand" style="display: none; height: 0; overflow: hidden;">').replace("<p>[/expand]</p>","</div>");e.parentNode.innerHTML=n}}),elements=document.querySelectorAll("div.expand"),Array.prototype.forEach.call(elements,function(e){e.previousElementSibling.innerHTML=e.previousElementSibling.innerHTML+`<span>..&nbsp; <a href="#" style="cursor: pointer;" onclick="this.parentNode.parentNode.nextElementSibling.style.display = 'block'; this.parentNode.parentNode.nextElementSibling.style.height = 'auto'; this.parentNode.style.display = 'none';">read&nbsp;more&nbsp;&rarr;</a></span>`})